package com.kthcorp.study.basic;

public class BasicCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 11;
		int b = 0;
		
		if ( a != 10 )
		{
			System.out.println("a > 10  and a < 10");
		} else if (b != 0)
		{
			System.out.println("b is not 0");
		} else
		{
			System.out.println("a is 10 and b is 0");
		}
	}

}
